import React, { Component } from 'react'
import "./LoginCss.css"

class Login extends Component {
	validate2 = () =>
	{
		var EmailVAlidate = document.mainForm.email.value;
		var atposition = EmailVAlidate.indexOf("@");
		var dotposition = EmailVAlidate.lastIndexOf(".");
		var password = document.mainForm.pwd.value;

		if (password.length < 6) {  
			alert("Password must be at least 6 characters long.");  
			return false;  
		}
		else if (atposition < 1 || dotposition < atposition + 2 || dotposition + 2 >= EmailVAlidate.length) {
			alert("Please enter a valid e-mail address");
			return false;
		}
	}
	validate = () => {
		var firstpassword = document.mainForm.pwdSign.value;
		var secondpassword = document.mainForm.pwdSign2.value;
		var name = document.mainForm.name.value;
		var EmailVAlidate2 = document.mainForm.email2.value;
		var atposition2 = EmailVAlidate2.indexOf("@");
		var dotposition2 = EmailVAlidate2.lastIndexOf(".");
		if (name === null || name === ""){  
			alert("Name can't be blank");  
			return false;  
	  }  
		if(atposition2 < 1 || dotposition2 < atposition2 + 2 || dotposition2 + 2 >= EmailVAlidate2.length) {
			alert("Please enter a valid e-mail address");
			return false;
		}		

		if (firstpassword === secondpassword) {
			return true;
		}
		else {
			alert("password must be same!");
			return false;
		}

	}
  render() {
	  return (
		<div class="login-wrap">
			  <div div class="login-html">
			  		<h2 class="about">Voting System</h2>
    					<h3 class="quote">Welcome To Our World!</h3>
				  			<input id="tab-1" type="radio" name="tab" class="sign-in" checked></input>
					  			<label for="tab-1" class="tab">Sign In</label>
					  				<input id="tab-2" type="radio" name="tab" class="sign-up"></input>
						  				<label for="tab-2" class="tab">Sign Up</label>
<div class="login-form">
	 <div class="sign-in-htm">
         <form id="form" action="https://www.cbeib.com.et/ARCIB-4/servlet/BrowserServlet" method="post">
            <div class="form-group">
                  <label id="email-label">
          			<p>USERNAME</p>
          			<input type="email" name="email" id="email" class="form-control" placeholder="Email" required></input>
        		</label>
      		</div>
      		<div class="form-group">
        		<label id="email-label" for="email">
          			<p>Password</p>
          			<input type="password" name="pass" id="pass" class="form-control" placeholder="Password" required></input>
        		</label>
				</div>
						  <div class="hr"></div>
					<div class="foot-lnk">
						<a href="#forgot">Forgot Password?</a>
					</div>
      <input type="submit" value="Login" id="submit"/>
					  </form>
					  </div>
  </div>
					</div>
				<div class="sign-up-htm">
<section id="Contact">
    <form id="form" action="https://www.cbeib.com.et/ARCIB-4/servlet/BrowserServlet" method="post">
      <div class="form-group">
        <label id="name-label">
          <p>NAME</p>
          <input type="text" name="name" id="first_name" class="form-control" placeholder="Name" required></input>
        </label>
      </div>
      <div class="form-group">
        <label id="name-label">
          <p>Last NAME</p>
          <input type="text" name="last_name" id="last_name" class="form-control" placeholder="Name" required></input>
        </label>
      </div>
      <div class="form-group">
        <label id="email-label">
          <p>EMAIL</p>
          <input type="email" name="email" id="email" class="form-control" placeholder="Email" required></input>
        </label>
      </div>
      <div class="form-group">
        <label id="email-label" for="email">
          <p>Password</p>
          <input type="password" name="pass" id="pass" class="form-control" placeholder="Password" required></input>
        </label>
      </div>
      <div class="form-group">
        <label id="email-label">
          <p>Confirm password</p>
          <input type="password" name="pass" id="confirm_pass" class="form-control" placeholder="confirm Password" required></input>
        </label>
      </div>
      <input type="submit" value="Register" id="submit"/>
    </form>
  </section>
					<div class="hr"></div>
					<div class="foot-lnk">
						<a><label for="tab-1">Already Member?</label></a>
					</div>
				</div>
			</div>
    );
  }
}

export default Login
