import React, { Component } from 'react'
import "./LoginCss.css"

class Login extends Component {
	validate2()
	{
		var EmailVAlidate = document.mainForm.email.value;
		var atposition = EmailVAlidate.indexOf("@");
		var dotposition = EmailVAlidate.lastIndexOf(".");
		var password = document.mainForm.pwd.value;

		if (password.length < 6) {  
			alert("Password must be at least 6 characters long.");  
			return false;  
		}
	}
	validate = () => {
		var firstpassword = document.mainForm.pwdSign.value;
		var secondpassword = document.mainForm.pwdSign2.value;
		var name = document.mainForm.name.value;
		var EmailVAlidate2 = document.mainForm.email2.value;
		var atposition2 = EmailVAlidate2.indexOf("@");
		var dotposition2 = EmailVAlidate2.lastIndexOf(".");
		if (name === null || name === ""){  
			alert("Name can't be blank");  
			return false;  
	  }  
		if(atposition2 < 1 || dotposition2 < atposition2 + 2 || dotposition2 + 2 >= EmailVAlidate2.length) {
			alert("Please enter a valid e-mail address");
			return false;
		}		


		if (firstpassword === secondpassword) {
			return true;
		}
		else {
			alert("password must be same!");
			return false;
		}

	}
  render() {
	  return (

		<div class="login-wrap">
			<form action = "" name = "mainForm" method = "post" onSubmit = {this.validate}>
		<div class = "login-html">
          <input id = "tab-1" type = "radio" name = "tab" class="sign-in" checked></input>
            <label htmlfor = "tab-1" class = "tab">Login</label>
          <input id = "tab-2" type = "radio" name = "tab" class = "sign-up"></input>
				<label htmlfor="tab-2" class="tab">Sign Up</label>
			<div class = "login-form">
				<div class = "sign-in-htm">
					<div class = "group">
						<label htmlfor = "user" class="label">Username</label>
						<input id = "user" type = "email" class = "input" name = "email"></input>
					</div>
					<div class = "group">
						<label htmlfor = "pass" class = "label">Password</label>
						<input id = "pass" type="password" class = "input" data-type = "password" name = "pwd"></input>
					</div>
					<div class = "group">
						<input id = "check" type="checkbox" class = "check" ></input>
						<label htmlfor = "check"><span class="icon"></span> Remember me?</label>
					</div>
					<div class = "group">
						<input type = "submit" class = "button" value = "Login"></input>
					</div>
					<div class = "hr"></div>
					<div class = "foot-lnk">
						<a href = "#forgot">Forgot Password?</a>
					</div>
				</div>
				<div class = "sign-up-htm">
					<div class = "group">
						<label htmlfor = "user" class = "label">Username</label>
						<input id = "user" type = "text" class = "input" name = "name"></input>
					</div>
					<div class = "group">
						<label htmlfor = "pass" class = "label">Password</label>
						<input id = "pass" type = "password" class = "input" data-type = "password" name = "pwdSign"></input>
					</div>
					<div class="group">
						<label htmlfor = "pass" class = "label">Repeat Password</label>
						<input id = "pass" type = "password" class = "input" data-type = "password"  name = "pwdSign2"></input>
					</div>
					<div class = "group">
						<label htmlfor = "pass" class = "label">Email Address</label>
						<input id = "pass" type = "email" class = "input" name = "email2"></input>
					</div>
					<div class = "group">
						<input type = "submit" class = "button" value = "Sign Up"></input>
					</div>
					<div class = "hr"></div>
					<a class = "foot-lnk">
              <label htmlfor = "tab-1">Already Member?</label></a>
          </div>
				</div>
				</div>  
				</form>
			</div>

    );
  }
}

export default Login
