import React, { Component } from 'react'
import "./LoginCss.css"

class Login extends Component {
validate = ()=> {
		var EmailVAlidate = document.mainForm.email.value;  
		var atposition = EmailVAlidate.indexOf("@");  
		var dotposition = EmailVAlidate.lastIndexOf(".");  
		var firstpassword = document.mainForm.pwdSign.value;  
		var secondpassword = document.mainForm.pwdSign2.value;  
		var name = document.mainForm.name.value;  
		var password = document.mainForm.pwd.value;  


		if (atposition < 1 || dotposition < atposition + 2 || dotposition + 2 >= EmailVAlidate.length){  
			document.getElementsByclassName('input').innerHtml("please insert valid email") 
			return false;  
		}  

		if(firstpassword === secondpassword){  
			return true;  
		}  
		else{  
				alert("password must be same!");  
				return false;  
			}  

		if (name === null || name === ""){  
			  alert("Name can't be blank");  
			  return false;  
		}
		else if (password.length < 6) {  
			  alert("Password must be at least 6 characters long.");  
			  return false;  
		}  
	}
  render() {
	  return (

		<div className="login-wrap">
		<div className = "login-html">
          <input id = "tab-1" type = "radio" name = "tab" className="sign-in" checked></input>
            <label htmlfor = "tab-1" className = "tab">Login</label>
          <input id = "tab-2" type = "radio" name = "tab" className = "sign-up"></input>
				  <label htmlfor="tab-2" className="tab">Sign Up</label>
		< form action = "" name = "mainForm" method = "post" onSubmit = {this.validate}>
			<div className = "login-form">
				<div className = "sign-in-htm">
					<div className = "group">
						<label htmlfor = "user" className="label">Username</label>
						<input id = "user" type = "email" className = "input" name = "email"></input>
					</div>
					<div className = "group">
						<label htmlfor = "pass" className = "label">Password</label>
						<input id = "pass" type="password" className = "input" data-type = "password" name = "pwd"></input>
					</div>
					<div className = "group">
						<input id = "check" type="checkbox" className = "check" ></input>
						<label htmlfor = "check"><span className="icon"></span> Remember me?</label>
					</div>
					<div className = "group">
						<input type = "submit" className = "button" value = "Login"></input>
					</div>
					<div className = "hr"></div>
					<div className = "foot-lnk">
						<a href = "#forgot">Forgot Password?</a>
				  </div>
						  </div>
						  </div>
				</form>
				 <form action = "" name = "mainForm" method = "post" onSubmit = {this.validate}>
				<div className = "sign-up-htm">
					<div className = "group">
						<label htmlfor = "user" className = "label">Username</label>
						<input id = "user" type = "text" className = "input" name = "name"></input>
					</div>
					<div className = "group">
						<label htmlfor = "pass" className = "label">Password</label>
						<input id = "pass" type = "password" className = "input" data-type = "password" name = "pwdSign"></input>
					</div>
					<div className="group">
						<label htmlfor = "pass" className = "label">Repeat Password</label>
						<input id = "pass" type = "password" className = "input" data-type = "password"  name = "pwdSign2"></input>
					</div>
					<div className = "group">
						<label htmlfor = "pass" className = "label">Email Address</label>
						<input id = "pass" type = "email" className = "input" name = "email"></input>
					</div>
					<div className = "group">
						<input type = "submit" className = "button" value = "Sign Up"></input>
					</div>
					<div className = "hr"></div>
					<a className = "foot-lnk">
              <label htmlfor = "tab-1">Already Member?</label></a>
					  </div>
					  </form>
				</div>
				</div>  

    );
  }
}

export default Login
